pragma solidity >=0.4.25 <0.6.0;

import "./SimpleCommit.sol";

contract ParImpar {
    using SimpleCommit for SimpleCommit.CommitType;
    address payable participant1;
    address payable participant2;
    uint256 funds = 0;

    bool ok;
    SimpleCommit.CommitType sc1;

    // no deploy do contrato os dois participantes são colocados para 
    // evitar que terceiros participem só para atrapalhar o jogo
    constructor(address payable _p1, address payable _p2) public {
        ok = false;
        participant1 = _p1;
        participant2 = _p2;
    }

      modifier isParticipant {
        if (msg.sender != participant1 || msg.sender != participant2)
            revert("Não participantes não podem participar");
      _;
   }    


    function doCommit(bytes32 c1) public payable isParticipant{
        sc1.commit(c1);
        funds += msg.value;
    }

    function doReveal(string memory v1, bytes32 nonce1) public isParticipant{
        sc1.reveal(nonce1,v1);
    }

    function hashData(string memory val, string memory nonce) public pure returns (bytes32) {
        return sha256(abi.encodePacked(nonce, val));
    }

    function getResult() public view returns (bool) {
        return ok;
    }
}
